		//simple Example of boolean data type
		int num1=100;
		int num2=200;
		boolean b1=true;
		boolean b2=false;
		if(num2>num1) {System.out.println("the cindition is"+"    "+b1);}
		else{System.out.println("the cindition is"+"     "+b2);}
		
